import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import "./Contact.css"


const Contact2 = (props) => {

    const [loading, setLoading] = useState(false)
    const history = useNavigate();
    const load = () => {
        if (loading) {
            return <div className="loading">Loading&#8230;</div>
        }
    };

    const handleSub = (e) => {
        e.preventDefault();
        setLoading(true);
        props.onSubmit()
        .then(() => setLoading(false))
        .then(() => history.push("/sent"))
    }

    return (
        <div className="container-form">
            {load()}
            <form id="contact-form" onSubmit={handleSub}>
                <div>
                    <input className="name" required placeholder="Name" onChange={props.name}></input>
                    <input className="email" required type="email" placeholder="E-Mail" onChange={props.mail}></input>
                </div>
                <textarea className="text" type="text" required placeholder="Your message" onChange={props.textarea}></textarea>
                <div className="buttons">
                    <button className="button" type="submit" value="Submit">Send</button>
                    <a className="button" href="https://www.linkedin.com/in/julian-m-pflueger/" target="_blank" rel="noreferrer">via LinkedIn instead</a>
                </div>
            </form>
        </div>
    )
};

export default Contact2;
